import React, { useState, useEffect } from 'react';
import axios from 'axios';

import ChatPage from './pages/chat-page.jsx';
import ConvoPage from './pages/conversation-page.jsx';
import AccessPage from './pages/access-page.jsx';
import RegisterPage from './pages/register-page.jsx';
import UsersPage from './pages/users-page.jsx';

axios.defaults.baseURL= "http://localhost:8080";
axios.defaults.headers.post['Content-Type']= 'application/x-www-form-urlencoded';

function App(){
  const [ currentPage, setCurrentPage ]= useState('');

  const changePage= (page)=>{
    setCurrentPage(page);
  }

  useEffect(()=>{
    const getMessage= async ()=>{
      await axios.get('/').then(res=>{
        console.log(res.data);
        changePage(res.data);
      });
    }
  getMessage(); 
  }, []);

  return (<div>
    {(currentPage === 'ChatPage')&& <ChatPage changePage= {changePage} />}
    {(currentPage === 'ConvoPage')&& <ConvoPage changePage= {changePage} />}
    {(currentPage === 'AccessPage')&& <AccessPage changePage= {changePage} />}
    {(currentPage === 'RegisterPage')&& <RegisterPage changePage= {changePage} />}
    {(currentPage === 'UsersPage')&& <UsersPage changePage= {changePage} />}
  </div>);
}
export default App;
