import { useState } from 'react';
import uuid from 'react-uuid';

const disArr= [['+ tasks to add', '00', '00'],['program', '02','00', '00'], ['read', '04', '00', '00'], ['write', '00', '30'], ['study', '50', '00', '00']];

function Display(props){
  const [task, setTask]= useState('+ tasks to add');

  return(<div className='display'>
    <div>
      <select name='tasks' id='tasks' onChange={(e)=>(setTask(e.target.value))}>
      {props.todayTracker().tasks.map( (item)=>(<option key={uuid()} value={item[0]} >{item[0]}</option>) )}
      </select>
      {props.todayTracker().filter( (item)=>(item.includes(task)) ).map( (item)=>(<span key={uuid()} className='time'>{item[1]}:{item[2]}</span>) )}
    </div>
  </div>)
};
export default Display
