import { useState } from 'react';

import { ExampleTasks } from '../tasks.js';
import TimePicker from './time-picker.jsx';

const dayOWeek= ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

const months= ['January', 'Feburary', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

function Events(props){
  const [month, dayNo, year]= props.clickedDate.split('/');
  const [timeState, setTimeState]= useState('');
  return(<div className='events'>
    <div className='head'>
      <h2>Todo</h2>
      <h3>{`${months[month-1]} ${dayNo} , ${year}`}</h3>
    </div>
    <div className='tasks'>{ ExampleTasks.tasks.map( (each)=>(<p>{each[0]}  </p>) ) }
    </div>
    <TimePicker setTimeState={setTimeState}/>
  </div>)
}
export default Events;
