import axios from 'axios';
import qs from 'qs';
import { Navigate } from 'react-router-dom';
import React, { useState } from 'react';

function AccessPage(props){
  const [username, setUsername]= useState('');
  const [password, setPassword]= useState('');
  const [redirect, setRedirect]= useState(false);

  const handleChange= (e)=>{
    if (e.target.name === 'username'){
      setUsername(e.target.value);
    }else if (e.target.name === 'password'){
      setPassword(e.target.value);
    }

  }

  const handleSubmit= async (e)=>{
    try{
      const data=  qs.stringify({username: username, password: password});
      const response= await axios.post("/login", data);
      if (response.data.auth){
        setRedirect(true);
      }
    }catch(err){
      console.log(err)
    }
  }

  return(<div className="access-container">
  { (redirect)&& <Navigate to='/' />}
  <div className="access">
    <div className="access-label">
      <span>Login</span>
    </div>
    <hr />
    <br />
    <div>
      <label>Username</label>
      <input name="username" placeholder="john_doe" onChange={handleChange}/>
    </div>
    <div>
      <label>Password</label>
      <input name="password" placeholder="password" onChange={handleChange}/>
    </div>
    <span className="signup" onClick={()=>(props.changePage('RegisterPage'))}><i className="fa fa-reply" aria-hidden="true"></i> <i>sign up</i></span>
    <button onClick={ handleSubmit }><i className="fa fa-sign-in" aria-hidden="true"></i></button>
  </div>
  </div>);
}
export default AccessPage;
