import StateTag from '../components/state-tag';
import Conversations from '../conversation.js';
function ConvoPage(props){
  const user= 'user1';
  return(<div>
    <StateTag
    page= {user}
    backTo= "ChatPage" 
    changePage= {props.changePage} />

    {Conversations.map( (eachConvo)=>(<div className="chat">
    <span className= {(Conversations.sender === user)? 'user-chat': 'friend-chat'}>{eachConvo.message}{eachConvo.sender}</span>
    </div>))}
  </div>);
}
export default ConvoPage;
