import { useState } from 'react';

const disArr= [['program', '02','00', '00'], ['read', '04', '00', '00'], ['write', '00', '00', '30'], ['study', '50', '00', '00']];

function Display(props){
  const [timer, getTimer]= useState('00:00');

  return(<div className='display'>
    <div>
      <select name='tasks' id='tasks'>
      {disArr.map( (item)=>(<option value={item[0]} >{item[0]}</option>) )}
      </select>
      {disArr.filter( (item)=>(item.includes(getTimer() )) ).map( (item)=>(<span className='time'>{item[1]}:{item[2]}</span>) )}
    </div>
  </div>)
};
export default Display
