import StateTag from '../components/state-tag';
import Conversations from '../conversation.js';
import send from '../images/send.svg';

function ConvoPage(props){
  const user= 'user1';
  return(<div className="convo">
    <StateTag
    page= {user}
    backTo= "ChatPage" 
    changePage= {props.changePage} />
    {Conversations.map( (eachConvo)=>(<div className="chat" key= {eachConvo.id}>
    <span className= {(eachConvo.sender === user)? 'common-chat user-chat': 'common-chat friend-chat'}>{eachConvo.message}</span>
    </div>))}
    <div className="compose">
      <input placeholder="write message"/>
      <img src= {send} />
    </div>
  </div>);
}
export default ConvoPage;
