import axios from 'axios';
import react, { useEffect, useState } from 'react';

function RegisterPage(props){
  const [ submit, setSubmit]= useState(false);

  useEffect( ()=>{
    const handleSubmit= async (e)=>{
      this.form.submit();
      await axios.get('/authenticate').then( res=>{
        if (res.data === 'authenticated'){
          props.changePage('ChatPage');
        }
      });
      e.preventDefault()
    }
  }, [setSubmit]);

  return(<form action='/register' method='post'> <div className="access-container">
  <div className="access">
    <div className="access-label">
      <span>Sign Up</span>
    </div>
    <hr />
    <br />
    <div>
      <label>Username</label>
      <input name="username" placeholder="john_doe"/>
    </div>
    <div>
      <label>Password</label>
      <input name="password" placeholder="password"/>
    </div>
    <button onClick={ ()=>(setSubmit(true)) }><i class="fa fa-sign-in" aria-hidden="true"></i></button>
  </div>
  </div></form>);
}
export default RegisterPage;
