function Calender(props){

  const { day, dayNo, month, year }= props.date();
  const dayOWeek= ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

  const months= ['January', 'Feburary', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

  function getDaysInMonth(year, month) {
    return (new Date(year, month, 0).getDate());
  }
  //alert(getDaysInMonth(year, month+1))

  var dt = new Date(`${month+1}/17/${year}`);

  return(<div className='calender'>
    <input type='date'/>
    <div className='calender-header'>
      <h4>{year}</h4>
      <h2>{dayOWeek[day].slice(0,3)}, {months[month]} {dayNo}</h2>
    </div>
    <div className='calender-body'>
      <ul>
        <li className="prev">&#10094;</li>
        <li className="next">&#10095;</li>
        <li className='mid'>{months[month]} {year}</li>
      </ul>
      <ul>
        {dayOWeek.map((day)=>(<li>{day.slice(0,1)}</li>))}
      </ul>
    </div>
  </div>)
}
export default Calender;
