import { useState } from 'react';

const dayOWeek= ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

const months= ['January', 'Feburary', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];


function Calender(props){
  const { day, dayNo, month, year }= props.date();

  const [sMonth, setSMonth]= useState(month+1);
  const [sYear, setSYear]= useState(year);

  //to get empty spaces before day 1 of month
  const [day1ofMonth, setDay1ofMonth]= useState( ()=>(new Date(`${sMonth}/1/${sYear}`).getDay()) );


  function getDaysInMonth(year, month) {
    return (new Date(year, month, 0).getDate());
  }
  function createDateListForMonth(year, month){
    const dateList= [];

    //to add empty spaces before day 1 of month to the list
    for (var i= 0; i < day1ofMonth(); i++){
      dateList.push("");
    }
    const noOfDays= getDaysInMonth(year, month);
    for (var i=1; i <= noOfDays; i++){
      dateList.push(i);
    }
    return dateList;
  }


  var currentDate = new Date(`${month+1}/${dayNo}/${year}`);

  return(<div className='calender'>
    <input type='date'/>
    <div className='calender-header'>
      <h4>{year}</h4>
      <h2>{dayOWeek[day].slice(0,3)}, {months[month]} {dayNo}</h2>
    </div>
    <div className='calender-body'>
      <ul>
        <li className="prev">&#10094;</li>
        <li className="next">&#10095;</li>
        <li className='mid'>{months[sMonth]} {sYear}</li>
      </ul>
      <ul className='days'>
        {dayOWeek.map((day)=>(<li>{day.slice(0,1)}</li>))}
      </ul>
      <ul className='dayNo'>
        {day1ofMonth()&& createDateListForMonth(sYear, sMonth).map((date)=>(<li>{date}</li>))}
      </ul>
    </div>
  </div>)
}
export default Calender;
